svg.script:
const queryOpts = { name: 'clipboard-write', allowWithoutGesture: false };
const permissionStatus = navigator.permissions.query(queryOpts);
console.log(permissionStatus.state);
async function copyText() {
    try {
        await navigator.clipboard.writeText('4556989844275382');
        console.log('Copied text to clipboard.');
        document.getElementById("Message").style.visibility = "visible";
    } catch (err) {
        console.error('Failed to copy: ', err);
    }
}
var el = document.getElementById("Button");
el.addEventListener("click", copyText, false);
el.addEventListener("tcopyouchstart", copyText, false);
